mapper.select:
select b.* FROM blog_info b,type_info t where b.type_id = t.type_id
and b.type_id = #{typeId,jdbcType=INTEGER}
and b.blog_title like "%"#{blogTitle,jdbcType=VARCHAR}"%"

mapper.insert:

insert into blog_info (blog_id, blog_title, blog_abstract, 
  blog_author, create_date, type_id, 
  lable_name, blog_url, blog_cover, 
  top_switch, recommend_switch, blog_uuid
  )
values (#{blogId,jdbcType=INTEGER}, #{blogTitle,jdbcType=VARCHAR}, #{blogAbstract,jdbcType=VARCHAR}, 
  #{blogAuthor,jdbcType=VARCHAR}, #{createDate,jdbcType=TIMESTAMP}, #{typeId,jdbcType=INTEGER}, 
  #{lableName,jdbcType=VARCHAR}, #{blogUrl,jdbcType=VARCHAR}, #{blogCover,jdbcType=VARCHAR}, 
  #{topSwitch,jdbcType=INTEGER}, #{recommendSwitch,jdbcType=INTEGER}, #{blogUuid,jdbcType=VARCHAR}
  )
